import React, { useState, useCallback } from 'react';
import Value from "./components/Value";
import {Button} from "./components/Button";
import "./styles.css";
import { useAppDispatch, useAppSelector } from "./redux/hooks";
import { thunkChangeByValue, thunkDecrementValue, thunkIncrementValue, thunkInputValue } from "./redux/slicesAndThunks/thunks";

const App = () => {
  const value = useAppSelector((store) => store.valueSlice.value);
  const additional = useAppSelector((store) => store.valueSlice.input);
  const [isFetching, setIsFetching] = useState(false);
  const dispatch = useAppDispatch();

  const handleIncrement = useCallback(() => {
    if (!isFetching) {
      setIsFetching(true);
      dispatch(thunkIncrementValue(value))
        .then(() => setIsFetching(false))
        .catch(() => setIsFetching(false));
    }
  }, [value]);

  const handleDecrement = useCallback(() => {
    if (!isFetching) {
      setIsFetching(true);
      dispatch(thunkDecrementValue(value))
        .then(() => setIsFetching(false))
        .catch(() => setIsFetching(false));
    }
  }, [value]);

  const handleChangeByValue = useCallback((newValue: number) => {
    if (!isFetching) {
      setIsFetching(true);
      dispatch(thunkChangeByValue({ value, additional: newValue }))
        .then(() => setIsFetching(false))
        .catch(() => setIsFetching(false));
    }
  }, [value, additional]);

  const handleInputChange = (newValue: number) => {
    dispatch(thunkInputValue(newValue));
  };

  return (
    <div className="App">
      <Value />
      <div>
        <Button btnStyle='plus_btn' text="Увеличить" isFetching={isFetching} onClick={handleIncrement} />
        <Button btnStyle='minus_btn' text="Уменьшить" isFetching={isFetching} onClick={handleDecrement} />
      </div>
      <div>
        <input
          placeholder="изменить на значение"
          className="input"
          type='number'
          onChange={({ target }) => {
            handleInputChange(+target.value);
          }}
        />
        <div>
          <Button btnStyle='plus_btn' isFetching={isFetching} onClick={() => handleChangeByValue(additional)} text="Увеличить на значение" />
          <Button btnStyle='minus_btn' isFetching={isFetching} onClick={() => handleChangeByValue(-additional)} text="Уменьшить на значение" />
        </div>
      </div>
    </div>
  );
};

export default App;
